import { createDrawerNavigator } from '@react-navigation/drawer';

import React, { Component } from 'react';
import { SafeAreaView, StyleSheet, View, Text } from 'react-native';

import Home from '../Home/home'
import Login from '../user/login'
import Edicao from '../user/edicao'
import Lista from '../lista_lojas/lista';

const Drawer = createDrawerNavigator()

export default class drawer extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            nomePag : ''
        }
    }

    render()
    {
        return(
            <Drawer.Navigator
            options={({ route }) => ({ title: route.params.name })}
            screenOptions={{
                headerTintColor: 'white',
                headerTitleAlign: 'center',
                headerStyle : {
                    backgroundColor : '#00873B',
                    alignItems: 'center',
                    justifyContent: 'center',
                },

                drawerActiveTintColor : '#FFF',
                drawerInactiveTintColor : '#FFF',
                drawerPosition : 'left',
                drawerStyle : {
                  backgroundColor :'#00873B',
                  borderRadius : 20,
                  height: '50%',
                  width: '50%',
                },
            }}>
                <Drawer.Screen name="Home" component={Home}/>
                <Drawer.Screen options name="Login" component={Login}/>
                <Drawer.Screen name="Lista" component={Lista}/>
                <Drawer.Screen name="Edição" component={Edicao}/>
            </Drawer.Navigator>
        )
    }
}

const styles = StyleSheet.create({

});
