import React, { Component } from "react";
import { View, Text, StyleSheet, Style, TouchableOpacity, ImageBackground, Image } from "react-native";
import { ScrollView, TextInput } from "react-native-gesture-handler";
import { TextInputMask } from 'react-native-masked-text'
import { max } from "react-native-reanimated";


export default class cadastroUser extends Component{
    constructor(props)
    {
        super(props);
        this.state =
        {
            cnpj: '',
            CEP: '',
        }
    }

    render()
    {
        return(
            <View style={styles.container}>

              <View style={styles.ctnH1}>
                <TouchableOpacity style={styles.TouchH1}>
                  <Image style={styles.imgH1} source={require('../../../assets/Arrow.svg')}/>
                </TouchableOpacity>
                <Text style={styles.txtH1}>Loja</Text>
              </View>


            <View style={styles.ctn}>

              <View style={styles.ctnInput}>

                <View style={styles.inputStyle}>
                  <TextInput
                    style={styles.TxtInput}
                    placeholder="Nome da loja"
                    placeholderTextColor="black"
                    keyboardType="default"
                  />

                  <TextInputMask
                  type={'cnpj'}
                  value={this.state.cnpj}
                  onChangeText={ x =>
                  {
                    this.setState({
                      cnpj : x
                    })
                  }}
                    style={styles.TxtInput}
                    placeholder="CNPJ"
                    placeholderTextColor="black"
                  />
                </View>

                <View style={styles.inputStyle}>
                <TextInputMask
                type={'custom'}
                options={{
                  mask : '99999-999'
                }}
                value={this.state.CEP}
                onChangeText={ x => {
                  this.setState({
                    CEP : x
                  })
                }}
                    style={styles.TxtInput}
                    placeholder="CEP"
                    placeholderTextColor="black"
                  />

                  <TextInput
                    style={styles.TxtInput}
                    placeholder="Rua"
                    placeholderTextColor="black"
                  />
                </View>


                <View style={styles.inputStyle}>
                <TextInput
                    style={styles.TxtInput}
                    placeholder="Endereço"
                    placeholderTextColor="black"
                  />
                   <TextInput
                    style={styles.TxtInput}
                    placeholder="Complemento"
                    placeholderTextColor="black"
                  />
                </View>

                <View style={styles.inputBig}>
                  <TextInput style={styles.inputDesc}
                    placeholder="Descrição"
                    placeholderTextColor="black"
                  />

                  <TextInput style={styles.InputImg}
                    placeholder='Selecione uma imagem da loja'
                    placeholderTextColor="black"
                    multiline="true"
                    ali
                    numberOfLines='2'
                  />
                </View>

                <View style={styles.btnInput}>
                  <TouchableOpacity style={styles.btnCtn}>
                    <Image style={styles.arrow} source={require('../../../assets/Black.svg')}/>
                  </TouchableOpacity>
            </View>
                </View>
              </View>

            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
    flex: 1,

  },

  imgH1: {
    width: '50%',
    height: '100%',
  },

  ctnH1: {
    flex: 0.3,
    backgroundColor: '#00873B',

    alignItems: 'center',
    flexDirection: 'row',
  },
  
  TouchH1: {
    width: '20%',
    height: '10%',
    // backgroundColor : 'blue',

    justifyContent: 'center',
    alignItems: 'center',
  },

  txtH1: {
    fontSize: 25,
    fontWeight: 'bold',
    color: 'white',
  },

  ctnInput : {
    flex: 0.9,
    // backgroundColor: 'blue',

    justifyContent: 'space-around',
  },

  ctn: {
    flex: 0.7,
    // backgroundColor : 'blue',

    justifyContent: 'center'
  },

  inputStyle: {
    flex: 0.12,
    // backgroundColor: 'yellow',

    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',

  },

  TxtInput: {
    width: '40%',
    height: '70%',

    borderColor: '#00873B',
    borderWidth: 4,
    borderRadius: 10,

    fontSize: 17,
    paddingLeft: 7,
  },

  btnInput: {
    flex: 0.1,
    // backgroundColor: 'orange',

    justifyContent: 'center',
    alignItems: 'center',
  },

  btnCtn: {
    width: '35%',
    height: '80%',
    // backgroundColor: '#008763',;

    justifyContent: 'center',
    alignItems: 'center',

    borderRadius: 10,
  },

  txtBtn: {
    color: 'white',
    fontSize: 20,
    fontFamily : 'Arial',
  },

  arrow: {
    color: 'white',
    width: '60%',
    height: '80%'
  },

  inputBig: {
    flex: 0.25,
    backgroundColor: 'red',

    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
  },

  inputDesc: {
    height: '100%',
    width: '40%',

    borderColor: '#00873B',
    borderWidth: 4,
    borderRadius: 10,

    fontSize: 17,
    paddingLeft: 7,
  },

  InputImg: {
    height: '80%',
    width: '40%',

    borderColor: '#00873B',
    borderWidth: 4,
    borderRadius: 10,

    fontSize: 17,
    paddingLeft: 7,

    textAlign: 'center',
    textAlignVertical: 'center',
  },

})
