import React, { Component } from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Image } from 'react-native';
import { TextInputMask } from 'react-native-masked-text'

export default class Lista extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            lista : [],
        }
    }

    render()
    {
        return(
         <View style={styles.container}>

             <View style={styles.listaCtn}>

                <View style={styles.ImgCtn}>
                    <View style={styles.Img}>
                    </View>
                </View>

                <View style={styles.arrow}>
                    <TouchableOpacity style={styles.touchArrow}>
                        <Image style={styles.ArrowDown} source={require('../../../assets/flecha_baixo.png')}/>
                    </TouchableOpacity>
                </View>

                <View style={styles.lista}>

                    <View style={styles.h1Lista}>
                        <Text style={styles.h1}>Nome da loja</Text>
                    </View>

                    <View style={styles.infsLista}>
                        <Text style={styles.txtLista}>3.5 estrelas</Text>
                        <Text style={styles.txtLista}>Https://siteEx.com.br</Text>
                        <Text style={styles.txtLista}>(11)98764-1243</Text>
                    </View>
                </View>

             </View>

         </View>   
        )
    }
}

const styles = StyleSheet.create({

    container: {
        flex: 1,
        flexDirection: 'column',
        alignItems: 'center',
    },

    listaCtn: {
        width: '85%',
        height: '20%',
        backgroundColor: 'green',

        borderRadius: 10,

        flexDirection: 'row',
    },

    ImgCtn: {
        width: '40%',
        height: '100%',
        // backgroundColor: 'gray',

        justifyContent: 'center',
        alignItems: 'center',
    },
    
    Img: {
        width: '90%',
        height: '80%',
        backgroundColor: 'gray',
    },

    lista: {
        width: '50%',
        height: '100%',
        backgroundColor: 'red',

        justifyContent: 'flex-end'
    },

    arrow: {
        width: '10%',
        height: '100%',
        backgroundColor: 'orange',

        justifyContent: 'flex-end'
    },

    h1Lista: {
        width: '100%',
        height: '25%',
        backgroundColor: 'blue'
    },

    h1: {
        color: 'white',
        textTransform: 'uppercase',
        fontWeight: 'bold',
    },

    infsLista: {
        height: '75%',
        width: '100%',
        backgroundColor: 'purple',

        justifyContent: 'space-around',
    },

    ArrowDown: {
        height: '100%',
        width: '100%',
    },

    touchArrow: {
        height: '15%',
        width: '100%',

        justifyContent: 'flex-end'
    },

    txtLista: {
        fontFamily: 'Arial',
        fontSize: '100%',
        color: 'white',
    },
})
